import axios from 'axios';
document.addEventListener("DOMContentLoaded", function() {
    const loginForm = document.querySelector('form');

    loginForm.addEventListener('submit', function(event) {

      // Prevent the form from submitting in the traditional way
      event.preventDefault();
  
      // Get the input values
      const email = event.target.querySelector('.form-control[type="text"]').value;
      const password = event.target.querySelector('.form-control[type="password"]').value;
      const rememberMe = event.target.querySelector('.form-check-input[type="checkbox"]').checked;
  
      // Logging the data to console
      console.log('Email:', email);
      console.log('Password:', password);
      console.log('Remember me:', rememberMe);
  
      // If you want to send this data to a server using Axios:
      
      axios.post('127.0.0.1:800', {
        email: email,
        password: password,
        rememberMe: rememberMe
      })
      .then(function(response) {
        console.log('Server Response:', response.data);
      })
      .catch(function(error) {
        console.error('Error:', error);
      });
      
    });
  });
  